import React, { useState } from "react";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import "./index.css";

const ImagePrediction = () => {
  const [image, setImage] = useState(null);
  const [prediction, setPrediction] = useState(null);
  const [probabilities, setProbabilities] = useState(null);

  const onAddImage = (event) => {
    const file = event.target.files[0];
    if (file) {
      const imageUrl = URL.createObjectURL(file);
      setImage(imageUrl);
      handlePrediction(file);
    }
  };

  const handlePrediction = async (file) => {
    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await fetch("http://127.0.0.1:5000/predict", {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        throw new Error("Failed to fetch prediction");
      }

      const data = await response.json();
      setPrediction(data.predicted_disease);
      setProbabilities(data.probabilities);
    } catch (error) {
      console.error("Error:", error);
    }
  };

  return (
    <div className="prediction-container" id="Prediction">
      <Container>
        <Row>
          <Col lg={12}>
            <h1 className="predict-heading">Predict Images</h1>
          </Col>
          <Col lg={5}>
            <div className="image-prediction-container">
              <div className="input-container">
                <input
                  className="input"
                  id="upload-button"
                  type="file"
                  accept="image/*"
                  onChange={onAddImage}
                />
              </div>
              <div className="image-container">
                {image && (
                  <img src={image} alt="Uploaded" className="uploaded-image" />
                )}
              </div>
              <div>
                <p className="note">*Choose the Algorithm</p>
                <div className="btn-container">
                  <button className="predict-btn Ml">Machine Learning</button>
                </div>
                <p className="note-2">
                  Note:
                  <br />
                  Currently, This system detects Prawn Health Conditions,
                  classifying them as Healthy, Black Gill, White Spot Disease.
                  Simply upload an image to get instant results.
                </p>
              </div>
            </div>
          </Col>
          <Col lg={7}>
            <div className="output-container">
              <Row>
                <Col lg={12}>
                  <div className="output-heading">
                    <h1>Prediction Results</h1>
                  </div>
                </Col>
                <Col lg={6}>
                  <div className="output-description">
                    <h1 className="result-heading">Prediction:</h1>
                    <div className="result-container">
                      <p>{prediction}</p>
                    </div>
                  </div>
                </Col>
                <Col lg={6}>
                  <div className="output-description">
                    <h1 className="result-heading">Probabilities:</h1>
                    <div className="result-container">
                      {probabilities && (
                        <ul>
                          {Object.entries(probabilities).map(([key, value]) => (
                            <li key={key}>
                              {key}: {value.toFixed(2)}
                            </li>
                          ))}
                        </ul>
                      )}
                    </div>
                  </div>
                </Col>
                <Col lg={12}>
                  <div className="output-description">
                    <h1 className="result-heading">Description:</h1>
                    <div className="result-container">
                      <p>{/* Add any further description if needed */}</p>
                    </div>
                  </div>
                </Col>
              </Row>
            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default ImagePrediction;
