import React, { useState } from "react";
import axios from "axios";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import "./index.css";

const ImagePrediction = () => {
  const [image, setImage] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const [prediction, setPrediction] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  // Handle Image Selection
  const onAddImage = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      const imageUrl = URL.createObjectURL(file);
      setImage(imageUrl);
    }
  };

  // Handle Machine Learning Prediction
  const handleMLPrediction = async () => {
    if (!selectedFile) {
      alert("Please select an image first.");
      return;
    }

    setLoading(true);
    setError(null);
    setPrediction(null);

    const formData = new FormData();
    formData.append("file", selectedFile);

    try {
      const response = await axios.post("http://127.0.0.1:5000/predict?model=ml", formData);
      console.log("üîç API Response:", response.data); // ‚úÖ Debugging
      setPrediction(response.data);
    } catch (err) {
      console.error("‚ùå API Error:", err); // ‚úÖ Debugging
      setError("Failed to fetch prediction. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="prediction-container" id="Prediction">
      <Container>
        <Row>
          <Col lg={12}>
            <h1 className="predict-heading">Predict Images</h1>
          </Col>
          <Col lg={5}>
            <div className="image-prediction-container">
              <div className="input-container">
                <input
                  className="input"
                  id="upload-button"
                  type="file"
                  accept="image/*"
                  onChange={onAddImage}
                />
              </div>
              <div className="image-container">
                {image && <img src={image} alt="Uploaded" className="uploaded-image" />}
              </div>
              <div>
                <p className="note">*Choose the Algorithm</p>
                <div className="btn-container">
                  <button className="predict-btn Ml" onClick={handleMLPrediction} disabled={loading}>
                    {loading ? "Processing..." : "Machine Learning"}
                  </button>
                </div>
                <p className="note-2">
                  Note:
                  <br />
                  Currently, This system detects Prawn Health Conditions,
                  classifying them as Healthy, Black Gill, or White Spot Disease.
                  Simply upload an image to get instant results.
                </p>
              </div>
            </div>
          </Col>
          <Col lg={7}>
            <div className="output-container">
              <Row>
                <Col lg={12}>
                  <div className="output-heading">
                    <h1>
                      {prediction ? `Predicted Disease: ${prediction.predicted_disease}` : "Output will appear here"}
                    </h1>
                  </div>
                </Col>
                <Col lg={6}>
                  <div className="output-description">
                    <h1 className="result-heading">Prediction:</h1>
                    <div className="result-container">
                      {prediction ? (
                        <p><strong>Probability:</strong> {prediction.probabilities[prediction.predicted_disease]?.toFixed(4)}</p>
                      ) : (
                        <p>No prediction yet.</p>
                      )}
                    </div>
                  </div>
                </Col>
                <Col lg={6}>
                  <div className="output-description">
                    <h1 className="result-heading">Tips:</h1>
                    <div className="result-container">
                      <p>Ensure proper biosecurity measures to prevent the spread of diseases.</p>
                    </div>
                  </div>
                </Col>
                <Col lg={12}>
                  <div className="output-description">
                    <h1 className="result-heading">Description:</h1>
                    <div className="result-container">
                      {prediction ? (
                        <p>Detailed information about {prediction.predicted_disease}.</p>
                      ) : (
                        <p>No description available.</p>
                      )}
                    </div>
                  </div>
                </Col>
              </Row>
              {error && <p className="error-message">{error}</p>}
            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default ImagePrediction;
