import React, { useState } from "react";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import "./index.css";
import axios from "axios";
import diseasesData from "./diseasesData";  // Import the diseases data

const ImagePredection = () => {
  const [image, setImage] = useState(null);
  const [prediction, setPrediction] = useState("");
  const [probabilities, setProbabilities] = useState({});
  const [diseaseInfo, setDiseaseInfo] = useState({ description: "" });

  const onAddImage = (event) => {
    const file = event.target.files[0];
    if (file) {
      const imageUrl = URL.createObjectURL(file);
      setImage(imageUrl);
    }
  };

  const onPredict = async () => {
    if (!image) {
      alert("Please upload an image first.");
      return;
    }

    const formData = new FormData();
    const file = document.getElementById("upload-button").files[0];
    formData.append("file", file);

    try {
      const response = await axios.post("http://127.0.0.1:5000/predict", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });
      const { predicted_disease, probabilities } = response.data;
      setPrediction(predicted_disease);
      setProbabilities(probabilities);

      // Get disease info based on the prediction
      const diseaseDetails = diseasesData[predicted_disease] || { description: "No description available." };
      setDiseaseInfo(diseaseDetails);
    } catch (error) {
      console.error("Error during prediction", error);
      alert("There was an error with the prediction.");
    }
  };

  return (
    <div className="predection-container" id="Prediction">
      <Container>
        <Row>
          <Col lg={12}>
            <h1 className="predict-heading">Prediction</h1>
          </Col>
          <Col lg={5}>
            <div className="image-predection-container">
              <div className="input-container">
                <input
                  className="input"
                  id="upload-button"
                  type="file"
                  accept="image/*"
                  onChange={onAddImage}
                />
              </div>
              <div className="image-container">
                {image && <img src={image} alt="Uploaded" className="uploaded-image" />}
              </div>
              <div>
                <div className="btn-container">
                  <button className="predict-btn Ml" onClick={onPredict}>
                    Predict
                  </button>
                </div>
                <p className="note">
                  <strong>Note:</strong>
                  <br />
                  This system detects Prawn Health Conditions, classifying them as Healthy, Black Gill, or White Spot Disease.
                  Simply upload an image to get instant results.
                </p>
              </div>
            </div>
          </Col>
          <Col lg={7}>
            <div className="output-container">
              <Row>
                <Col lg={12}>
                  <div className="output-heading">
                    <h1>{prediction ? `${prediction}` : "Output"}</h1>
                  </div>
                </Col>
                <Col lg={6}>
                  <div className="output-predection">
                    <h1 className="result-heading">Probability:</h1>
                    <div className="result-container">
                      {Object.keys(probabilities).length > 0 && (
                        <>
                          {Object.entries(probabilities).map(([disease, prob]) => (
                            <p key={disease}>
                              {disease}: {prob.toFixed(4)}
                            </p>
                          ))}
                        </>
                      )}
                    </div>
                  </div>
                </Col>
                <Col lg={6}>
                  <div className="output-tips">
                    <h1 className="result-heading">Tips & Description:</h1>
                    <div className="result-container">
                      <p className="description-text">{diseaseInfo.description}</p>
                    </div>
                  </div>
                </Col>
              </Row>
            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default ImagePredection;
