import React, { useState } from "react";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import "./index.css";
import axios from "axios";
import diseasesData from "./diseasesData"; // Import the diseases data

const ImagePrediction = () => {
  const [image, setImage] = useState(null);
  const [prediction, setPrediction] = useState("");
  const [probabilities, setProbabilities] = useState({});
  const [diseaseInfo, setDiseaseInfo] = useState({ description: "", tips: [] });
  const [loading, setLoading] = useState(false);

  const onAddImage = (event) => {
    const file = event.target.files[0];
    if (file) {
      const imageUrl = URL.createObjectURL(file);
      setImage(imageUrl);
    }
  };

  const onPredict = async () => {
    if (!image) {
      alert("Please upload an image first.");
      return;
    }

    setLoading(true); // ✅ Start loading before sending request

    const formData = new FormData();
    const file = document.getElementById("upload-button").files[0];
    formData.append("file", file);

    try {
      const response = await axios.post("http://127.0.0.1:5000/predict", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      const { predicted_disease, probabilities } = response.data;
      setPrediction(predicted_disease);
      setProbabilities(probabilities);

      // Get disease info based on the prediction
      const diseaseDetails = diseasesData[predicted_disease] || {
        description: "No description available.",
        tips: [],
      };
      setDiseaseInfo(diseaseDetails);
    } catch (error) {
      console.error("Error during prediction", error);
      alert("There was an error with the prediction.");
    } finally {
      setLoading(false); // ✅ Stop loading after request completes
    }
  };

  return (
    <div className="prediction-container" id="Prediction">
      <Container>
        <Row>
          <Col lg={12}>
            <h1 className="predict-heading">Prediction</h1>
          </Col>
          <Col lg={5}>
            <div className="image-prediction-container">
              <div className="input-container">
                <input
                  className="input"
                  id="upload-button"
                  type="file"
                  accept="image/*"
                  onChange={onAddImage}
                />
              </div>
              <div className="image-container">
                {image && <img src={image} alt="Uploaded" className="uploaded-image" />}
              </div>
              <div>
                <div className="btn-container">
                  <button className="predict-btn" onClick={onPredict} disabled={loading}>
                    {loading ? "Processing..." : "Predict"}
                  </button>
                </div>
                <p className="note">
                  <strong>Note:</strong> Currently, this system detects Prawn Health Conditions,
                  classifying them as Healthy, Black Gill, or White Spot Disease. Simply upload an image
                  to get instant results.
                </p>
              </div>
            </div>
          </Col>
          <Col lg={7}>
            <div className="output-container">
              <Row>
                <Col lg={12}>
                  <div className="output-heading">
                    <h1>{loading ? "Analyzing image... Please wait." : prediction || "Output"}</h1>
                  </div>
                </Col>
                <Col lg={6}>
                  <div className="output-prediction">
                    <h1 className="result-heading">Probability:</h1>
                    <div className="result-container">
                      {loading ? (
                        <p>Processing...</p>
                      ) : Object.keys(probabilities).length > 0 ? (
                        Object.entries(probabilities).map(([disease, prob]) => (
                          <p key={disease}>
                            {disease}: {prob.toFixed(4)}
                          </p>
                        ))
                      ) : (
                        <p>No prediction yet.</p>
                      )}
                    </div>
                  </div>
                </Col>
                <Col lg={6}>
                  <div className="output-tips">
                    <h1 className="result-heading">Tips:</h1>
                    <ul className="result-container">
                      {loading ? (
                        <p>Processing...</p>
                      ) : diseaseInfo.tips.length > 0 ? (
                        diseaseInfo.tips.map((tip, index) => <li className="tip-text" key={index}>{tip}</li>)
                      ) : (
                        <p>No tips available.</p>
                      )}
                    </ul>
                  </div>
                </Col>
                <Col lg={12}>
                  <div className="output-description">
                    <h1 className="result-heading">Description:</h1>
                    <div className="result-container">
                      {loading ? <p>Processing...</p> : <p className="description-text">{diseaseInfo.description || "No description available."}</p>}
                    </div>
                  </div>
                </Col>
              </Row>
            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default ImagePrediction;
