import React, { useState } from "react";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import "./index.css";
import axios from "axios";
import diseasesData from "./diseasesData"; // Import the diseases data

const ImagePredection = () => {
  const [image, setImage] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const [prediction, setPrediction] = useState("");
  const [probabilities, setProbabilities] = useState({});
  const [individualPredictions, setIndividualPredictions] = useState({}); // State for individual model predictions
  const [bestModel, setBestModel] = useState("");
  const [diseaseInfo, setDiseaseInfo] = useState({ description: "", tips: [] });
  const [labeledImage, setLabeledImage] = useState(null);

  const onAddImage = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file); // Store the actual file for API call
      const imageUrl = URL.createObjectURL(file);
      setImage(imageUrl);
    }
  };

  const onPredict = async () => {
    if (!selectedFile) {
      alert("Please upload an image first.");
      return;
    }

    const formData = new FormData();
    formData.append("file", selectedFile);

    try {
      const response = await axios.post("http://127.0.0.1:5000/predict", formData, {
        headers: {
          "Content-Type": "multipart/form-data",
        },
      });

      const { predicted_disease, probabilities, individual_predictions, labeled_image_base64, best_model } = response.data;

      setPrediction(predicted_disease);
      setProbabilities(probabilities);
      setIndividualPredictions(individual_predictions); // Store individual model predictions
      setLabeledImage(labeled_image_base64);
      setBestModel(best_model);

      // Get disease info based on the prediction
      const diseaseDetails = diseasesData[predicted_disease] || { description: "No description available.", tips: [] };
      setDiseaseInfo(diseaseDetails);
    } catch (error) {
      console.error("Error during prediction", error);
      alert("There was an error with the prediction. Please try again.");
    }
  };

  return (
    <div className="predection-container" id="Prediction">
      <Container>
        <Row>
          <Col lg={12}>
            <h1 className="predict-heading">Prediction</h1>
          </Col>
          <Col lg={5}>
            <div className="image-predection-container">
              <div className="input-container">
                <input
                  className="input"
                  id="upload-button"
                  type="file"
                  accept="image/*"
                  onChange={onAddImage}
                />
              </div>
              <div className="image-container">
                {image && <img src={image} alt="Uploaded" className="uploaded-image" />}
              </div>
              <div>
                <div className="btn-container">
                  <button className="predict-btn Ml" onClick={onPredict}>
                    Predict
                  </button>
                </div>
                <p className="note-2">
                  Note:
                   <br />
                    For the best prediction and accurate results, please upload clear images of the prawn without any distractions. Ensure good lighting and focus on the affected area to improve probability scores and overall detection quality.
                </p>
                <p className="note">
                  Currently, this system detects Prawn Health Conditions, classifying them as Healthy, Black Gill, or White Spot Disease.
                  Simply upload an image to get instant results.
                </p>
              </div>
            </div>
          </Col>
          <Col lg={7}>
            <div className="output-container">
              <Row>
                <Col lg={12}>
                  <div className="output-heading">
                    <h1>{prediction ? `${prediction}` : "Output"}</h1>
                  </div>
                </Col>
                <Col lg={6}>
                  <div className="output-predection">
                    <h1 className="result-heading">Probability:</h1>
                    <div className="result-container">
                      {Object.keys(probabilities).length > 0 && (
                        <>
                          {/* Display Overall Disease Probabilities */}
                          {Object.entries(probabilities)
                            .sort((a, b) => b[1] - a[1]) // Sort probabilities in descending order
                            .map(([disease, prob]) => (
                              <p key={disease}>
                                <strong>{disease}:</strong> {prob.toFixed(4)}
                              </p>
                            ))}

                          {/* Display Best Model */}
                          {bestModel && (
                            <p>
                              <strong>Best Model:</strong> {bestModel}
                            </p>
                          )}
                        </>
                      )}
                    </div>
                  </div>
                </Col>
                <Col lg={6}>
                  <div className="output-predection">
                    <h1 className="result-heading">Individual Model Predictions:</h1>
                    <div className="result-container">
                      {Object.keys(individualPredictions).length > 0 ? (
                        <>
                          {Object.entries(individualPredictions).map(([model, pred]) => (
                            <p key={model}>
                              <strong>{model}:</strong> {pred.disease} ({(pred.probability * 100).toFixed(2)}%)
                            </p>
                          ))}
                        </>
                      ) : (
                        <p>No individual model predictions available.</p>
                      )}
                    </div>
                  </div>
                </Col>
                <Col lg={6}>
                  <div className="labeled_images">
                    <h1 className="result-heading">Labeled Image:</h1>
                    <div className="resultlabel-container">
                      <div className="result-label">
                        {labeledImage && <img src={`data:image/png;base64,${labeledImage}`} alt="Labeled Disease Area" className="labeled-image" />}
                      </div>
                    </div>
                  </div>
                </Col>
                <Col lg={12}>
                  <div className="output-decription">
                    <h1 className="result-heading">Description:</h1>
                    <div className="result-container">
                      <p className="description-text">{diseaseInfo.description}</p>

                      {/* Display Prevention Tips as a bullet-point list if available */}
                      {diseaseInfo.tips && diseaseInfo.tips.length > 0 && (
                        <div className="prevention-tips">
                          <h5><strong>Prevention Tips:</strong></h5>
                          <ul>
                            {diseaseInfo.tips.map((tip, index) => (
                              <li key={index}>{tip}</li>
                            ))}
                          </ul>
                        </div>
                      )}
                    </div>
                  </div>
                </Col>
              </Row>
            </div>
          </Col>
        </Row>
      </Container>
    </div>
  );
};

export default ImagePredection;
